								? false
								: true;

							if ($i % 3 === 0) {
								return 'item-big';
							} else if ($is_last_item_big and $i === $count_items) {
								return 'item-big';
							} else {
								return '';
							}
						}
						$items_arr = get_field('use_cases_grid');
						if ($items_arr) {
							$count_items = count($items_arr);
							$i = 0;

